mapper.select:
SELECT app_info.id, app_info.softwarename, app_info.apkname, app_info.softwaresize, app_info.downloads, app_info.versionid, app_info.status, pt.ptname, st.sname, t1.tname1, t2.tname2, t3.tname3 FROM app_info INNER JOIN (SELECT valueid,valuename AS ptname FROM `data_dictionary` WHERE typename='所属平台') AS pt ON app_info.`flatformId`= pt.valueid INNER JOIN (SELECT valueid,valuename AS sname FROM `data_dictionary` WHERE typename='APP状态') AS st ON app_info.`status`=st.valueid INNER JOIN (SELECT id,categoryName AS tname1 FROM `app_category`) AS t1 ON app_info.`categoryLevel1`=t1.id INNER JOIN (SELECT id,categoryName AS tname2 FROM `app_category`) AS t2 ON app_info.`categoryLevel2`=t2.id INNER JOIN (SELECT id,categoryName AS tname3 FROM `app_category`) AS t3 ON app_info.`categoryLevel3`=t3.id where 1 = 1 and devid = #{devid}
and softwarename like concat('%',#{querySoftwareName},'%')
and STATUS = #{queryStatus}
and FlatformId = #{queryFlatformId}
and CategoryLevel1 = #{queryCategoryLevel1}
and CategoryLevel2 = #{queryCategoryLevel2}
and CategoryLevel3 = #{queryCategoryLevel3}

SELECT app_info.id, app_info.softwarename, app_info.apkname, app_info.softwaresize, app_info.downloads, app_info.versionid, logoPicPath, `status`, pt.ptname, st.sname, t1.tname1, t2.tname2, t3.tname3 FROM app_info INNER JOIN (SELECT valueid,valuename AS ptname FROM `data_dictionary` WHERE typename='所属平台') AS pt ON app_info.`flatformId`= pt.valueid INNER JOIN (SELECT valueid,valuename AS sname FROM `data_dictionary` WHERE typename='APP状态') AS st ON app_info.`status`=st.valueid INNER JOIN (SELECT id,categoryName AS tname1 FROM `app_category`) AS t1 ON app_info.`categoryLevel1`=t1.id INNER JOIN (SELECT id,categoryName AS tname2 FROM `app_category`) AS t2 ON app_info.`categoryLevel2`=t2.id INNER JOIN (SELECT id,categoryName AS tname3 FROM `app_category`) AS t3 ON app_info.`categoryLevel3`=t3.id where STATUS=1
and softwarename like concat('%',#{querySoftwareName},'%')
and STATUS = #{queryStatus}
and FlatformId = #{queryFlatformId}
and CategoryLevel1 = #{queryCategoryLevel1}
and CategoryLevel2 = #{queryCategoryLevel2}
and CategoryLevel3 = #{queryCategoryLevel3}

SELECT app_info.id, app_info.softwarename, app_info.apkname, app_info.softwaresize, app_info.downloads, app_info.versionid, logoPicPath, status, appinfo, pt.ptname, st.sname, t1.tname1, t2.tname2, t3.tname3 FROM app_info INNER JOIN (SELECT valueid,valuename AS ptname FROM `data_dictionary` WHERE typename='所属平台') AS pt ON app_info.`flatformId`= pt.valueid INNER JOIN (SELECT valueid,valuename AS sname FROM `data_dictionary` WHERE typename='APP状态') AS st ON app_info.`status`=st.valueid INNER JOIN (SELECT id,categoryName AS tname1 FROM `app_category`) AS t1 ON app_info.`categoryLevel1`=t1.id INNER JOIN (SELECT id,categoryName AS tname2 FROM `app_category`) AS t2 ON app_info.`categoryLevel2`=t2.id INNER JOIN (SELECT id,categoryName AS tname3 FROM `app_category`) AS t3 ON app_info.`categoryLevel3`=t3.id where app_info.id=#{appid}
and softwarename like concat('%',#{querySoftwareName},'%')
and STATUS = #{queryStatus}
and FlatformId = #{queryFlatformId}
and CategoryLevel1 = #{queryCategoryLevel1}
and CategoryLevel2 = #{queryCategoryLevel2}
and CategoryLevel3 = #{queryCategoryLevel3}
